import React from "react";
import { Grid } from "@material-ui/core";
import SecurityIcon from '@material-ui/icons/Security';
import BrushIcon from '@material-ui/icons/Brush';
import DesktopMacIcon from '@material-ui/icons/DesktopMac';
import TrackChangesIcon from '@material-ui/icons/TrackChanges';
import AssignmentIcon from '@material-ui/icons/Assignment';
import FacebookIcon from '@material-ui/icons/Facebook';
import InstagramIcon from '@material-ui/icons/Instagram';
import YouTubeIcon from '@material-ui/icons/YouTube';
import TwitterIcon from '@material-ui/icons/Twitter';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import GitHubIcon from '@material-ui/icons/GitHub';


const About = () => {
    return(
        <React.Fragment>
            <div className = "about-me-grid">
            <div className = "inner-services-div">
            <h1>About Me</h1>
            <hr /> 
            </div>  
           <Grid item xs = {12} container>

               <Grid item lg = {6} md = {6} sm = {12} xm = {12} className = "about-me-grid">
                   <p>Hi there! this is Aniqa a UI/UX Designer and Software Developer. I am also a Web Application Penetration Tester / infrastructure pentester. I can design / develop your website with attractive and meaningful contents. Because i am also a content writer as well. I can build your website too attractive, responsive and highly secure as well.</p>
                   <p>I can be your next <strong>Web Designer / Developer, Pentester and Content Writer</strong> in future.</p>
                   <p><strong>Email:</strong>{" "}aniqaayub6@gmail.com</p>
                   <p><strong>Phone:</strong>{" "}+92 317 5637628</p>
                   <p><strong>City:</strong>{" "}Rawalpindi, Pakistan</p>
                   <a href = "#"><FacebookIcon id = "aboutIcon"/></a>
                   <a href = "#"><InstagramIcon id = "aboutIcon"/></a>
                   <a href = "#"><LinkedInIcon id = "aboutIcon"/></a>
                   <a href = "#"><TwitterIcon id = "aboutIcon"/></a>
                   <a href = "#"><YouTubeIcon id = "aboutIcon"/></a>
                   <a href = "#"><GitHubIcon id = "aboutIcon"/></a>

               </Grid>
               <Grid item lg = {6} md = {6} sm = {12} xm = {12} className = "about-me-grid">

                   <p><BrushIcon /> {" "}DESIGNING</p>
                   <div className="progress">
                   <div className="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width: '55rem', backgroundColor: 'black'}}></div>
                   </div>
                   <p><DesktopMacIcon />{" "}DEVELOPMENT</p>
                   <div className="progress">
                   <div className="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width: '40rem', backgroundColor: 'black'}}></div>
                   </div>
                   <p><SecurityIcon />{" "}PENTESTING</p>
                   <div className="progress">
                   <div className="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width: '30rem', backgroundColor: 'black'}}></div>
                   </div>
                   <p><TrackChangesIcon />{" "}MARKETING</p>
                   <div className="progress">
                   <div className="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width: '40rem', backgroundColor: 'black'}}></div>
                   </div>
                   <p><AssignmentIcon />{" "}CONTENT WRITING</p>
                   <div className="progress">
                   <div className="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuenow="75" aria-valuemin="0" aria-valuemax="100" style={{width: '35rem', backgroundColor: 'black'}}></div>
                   </div>
               </Grid>
           </Grid>
           </div>

        </React.Fragment>
    );
}

export default About;
